import React, {useState} from 'react';
import TodoForm from './TodoForm';


var dt = new Date();
var month = dt.getMonth()+1;
var date = dt.getDate();
var week = ["日","月","火","水","木","金","土"];
var day = week[dt.getDay()];

function TodoList(){
  

  const handleRemoveTask = index => {
    const newTodos = [...todos].filter((todo, todoIndex) => todoIndex !== index);
    setTodo(newTodos)
  }


  return(
    <div>
      <TodoForm />
      <h1>{month}月{date}日({day}曜日) TodoList</h1>
      <form onSubmit={handleSubmit}>
        タスクの追加: <input value={task} placeholder="新しいタスクを追加" onChange={handleNewTask}/>
      </form>
      <ul>
        {todos.map((todo, index) => (
          <li key={index} style={todo.isCompleted === true ? {textDecorationLine:'line-through'}:{}}>
            {todo.task}
            <button onClick={() => handleUpdateTask(index)}>{todo.isCompleted === true ? '戻す':'完了'}</button>
            <button onClick={() => handleRemoveTask(index)}>削除</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
