import React, {useState} from 'react';

var dt = new Date();
var month = dt.getMonth()+1;
var date = dt.getDate();
var week = ["日","月","火","水","木","金","土"];
var day = week[dt.getDay()];

function TodoList(){
  const initialState = [
    {
      task: 'aaa',
      isCompleted: false
    },
    {
      task: 'bbb',
      isCompleted: false
    },
    {
      task: 'ccc',
      isCompleted: false
    },
  ]

  const [todos, setTodo] = useState(initialState);
  const [task, setTask] = useState('')

  const handleNewTask = (event) => {
    setTask(event.target.value);
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    if(task === '') return
    setTodo(todos => [...todos, {task, isCompleted:false}])
    setTask('')
  }

  
  const handleRemoveTask = (index) => {
    console.log(index)
    const newTodos = [...todos]
    newTodos.splice(index,1)
    setTodo(newTodos)
  }

  // const handleRemoveTask = index => {
  //   const newTodos = [...todos].filter((todo, todoIndex) => todoIndex !== index);
  //   setTodo(newTodos)
  // }

  return(
    <div>
      <h1>{month}月{date}日({day}曜日) ToDo List</h1>
      <form onSubmit={handleSubmit}>
        Add Task: <input value={task} placeholder="Add New Task" onChange={handleNewTask}/>
      </form>
      <ul>
        {todos.map((todo, index) => (
          <li key={index}>{todo.task} <span onClick={() => handleRemoveTask}>X</span></li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
