import React, {useState} from 'react';

var dt = new Date();
var month = dt.getMonth()+1;
var date = dt.getDate();
var week = ["日","月","火","水","木","金","土"];
var day = week[dt.getDay()];

function TodoList(){
  const todomap = [
    {
      task: null,
      isCompleted: false
    },
  ]

  const [todos, setTodo] = useState(map);
  const [task, setTask] = useState('')

  const handleNewTask = (event) => {
    setTask(event.target.value);
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    if(task === '') return
    setTodo(todos => [...todos, {task, isCompleted:false}])
    setTask('')
  }

  const handleUpdateTask = index => {
    let newTodos = todos.map((todo, todoIndex) => {
      if(todoIndex === index){
        todo.isCompleted = !todo.isCompleted
      }
      return todo;
    })
    setTodo(newTodos)
  }

  const handleRemoveTask = index => {
    const newTodos = [...todos].filter((todo, todoIndex) => todoIndex !== index);
    setTodo(newTodos)
  }

  return(
    <div>
      <h1>{month}月{date}日({day}曜日) ToDo List</h1>
      <form onSubmit={handleSubmit}>
        Add Task: <input value={task} placeholder="Add New Task" onChange={handleNewTask}/>
      </form>
      <ul>
        {todos.map((todo, index) => (
          <li key={index} style={todo.isCompleted === true ? {textDecorationLine:'line-through'}:{}}>
            {todo.task}
            <button onClick={() => handleUpdateTask(index)}>完了</button>
            <button onClick={() => handleRemoveTask(index)}>削除</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
